create table if not exists member (id int not null auto_increment, firstname varchar(32) not null, lastname varchar(32) not null, email varchar(128), password varchar(128) not null, avatar varchar(512), created varchar(17) not null, updated varchar(17) not null, primary key(id), index(id), index(email));
create table if not exists schedule (id int not null auto_increment, memberid int not null, summary varchar(64) not null, guest text, memo text, startdatetime varchar(17) not null, enddatetime varchar(17) not null, created varchar(17) not null, updated varchar(17) not null, primary key(id), index(id), index(memberid), index(startdatetime), index(enddatetime), foreign key(memberid) references member(id));
